var path = require('path');
var DOMFactory = require('DOMFactory');

exports.reset = function(){
  $('#siblingsSection').empty();
  $('#currentDirectory').empty();
  $('#childSection').empty();
};

exports.setParent = function(p){
  $('#parentDirectory').text(path.basename(p));
};
  
exports.addFileToCurrentDirectory = function(p){
  $('#currentDirectory').append(DOMFactory.file(p));
};

exports.addFileToDirectory = function(p){
  $('[data-path="' + path.dirname(p) + '"]').append(
    DOMFactory.file(p)
    );
};

exports.addChildDirectory = function(p){
  $('#childSection').append(
    DOMFactory.folderView(p)
    );
};

exports.addSiblingDirectory = function(p){
  $('#siblingsSection').append(
    DOMFactory.folderView(p)
    );
};
  
exports.removeFile = exports.removeFolderView = function(p){
  $('[data-path="' + p + '"]').remove();
};

