var path = require('path');


exports.file = function(fileName){
  return $('<div>').addClass('file').attr('data-path', fileName).text(path.basename(fileName)).attr('draggable', 'true');
};

exports.folderView = function(dirName){
  return $("<div>").addClass('folderView').attr('data-path', dirName).text(path.basename(dirName)).append($("<br/>"));
};