global.$ = $;

$(global.window.document).ready(function(){
  
  

  var fs = require('fs');
var path = require('path');

  var start = '/Users/barnaj/Desktop';
fs.readdir(start, function(err, files){
  
  for (file of files){
    var filepath = path.join(start, file);
    var fileStats = fs.statSync(filepath);
    
    if (fileStats.isFile() && path.extname(file)){
      var newFile = $('<div>').addClass('fileOuter pure-u-1-4 pure-u-sm-1-8 pure-u-md-1-12')
        .append($('<div>').addClass('fileInner')
          .append($('<div>').addClass('fileNameShell')
            .append($('<span>').addClass('fileName').text(path.basename(file, path.extname(file))))
          )
        .append($('<br>'))
        .append($('<span>').addClass('extention').text(path.extname(file))));
      $('.fileHolder').append(newFile);
    }
  }
});
  

    
});
  